from abc import ABC, abstractmethod

class Pagamento(ABC):
    def __init__(self, valor: float, descricao: str):
        self.valor: float = valor
        self.descricao: str = descricao

    def validar_valor(self):
        if self.valor <= 0:
            raise ValueError("Valor negativo")

    def resumo(self):
        print(f"Pagamento de R$ {self.valor:.2f}: {self.descricao}")
        return

    @abstractmethod
    def processar(self):
        pass

class Pix(Pagamento):
    def __init__(self, valor: float, descricao: str, chave: str, banco: str):
        super().__init__(valor, descricao)
        self.chave = chave
        self.banco = banco

    def processar(self):
        print(f"Pagando pix produto {self.descricao} para {self.chave} do banco {self.banco} no valor de {self.valor}")


def processar_pagamento(pagamento: Pagamento):
    pagamento.validar_valor()
    pagamento.resumo()
    pagamento.processar()



class CartaodeCredito(Pagamento):
    def __init__(self, valor: float, descricao: str, numero:int, nome:str, limite:float):
        super().__init__(valor, descricao)
        self.numero = numero
        self.nome = nome
        self.limite = limite

    def processar(self):
        if self.valor > self.limite:
            print("Liso!!!")
            return
        self.limite -= self.valor
        print(f"Pagamento aprovado no cartão Cliente {self.nome}. Limite restante: {self.limite}")


class Boleto(Pagamento):
    def __init__(self, valor: float, descricao: str, codigo:str, vencimento:str):
        super().__init__(valor, descricao)
        self.codigo_barras = codigo
        self.vencimento = vencimento

    def processar(self):
        print("Boleto gerado. Aguardando pagamento...")

pagamentos = [
    Pix(150, "Camisa esportiva", "email@ex.com", "Banco XPTO"),
    CartaodeCredito(400, "Tênis esportivo", 1234567891234567, "Raimundo", 500),
    Boleto(89.90, "Livro de Python", "123456789000", "2025-01-10"),
    CartaodeCredito(800, "Notebook", 9999888877776666, "Cliente Y", 700)]

for i in pagamentos:
    i.processar()
